import React, { Component } from "react";
import emailjs from '@emailjs/browser';
import WhatsApp from "../../img/whatsapp-svgrepo-com.svg";
import "./Contacto.css";
import {Helmet} from "react-helmet";



export default class Contacto extends Component {

  constructor(){
    super();
    this.form = React.createRef();
  }
  state = {
    toggleMenu: false,
    screenWidth: window.innerWidth,
    animate: false,
  };

  toggleNav = () => {
    this.setState({ animate: true });
    this.setState({ toggleMenu: !this.state.toggleMenu });
  };

  scroll = (y) => {
    console.log(y);
    window.scroll(y, y);
  };

  sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', this.form.current, 'YOUR_PUBLIC_KEY')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  render() {
    return (
      <div>
        <div className="contactoContainer">
          <Helmet>
  <script type="text/javascript" id="hs-script-loader" async defer src="//js-eu1.hs-scripts.com/25963709.js"></script>
          </Helmet>
        </div>  
      </div>
    );
  }
}
