import { Component, useRef } from "react";
import emailjs from '@emailjs/browser';
import WhatsApp from "../../img/whatsapp-svgrepo-com.svg";
import "./Contacto.css";



export default class Contacto extends Component {
  state = {
    toggleMenu: false,
    screenWidth: window.innerWidth,
    animate: false,
    form : useRef(),
  };

  toggleNav = () => {
    this.setState({ animate: true });
    this.setState({ toggleMenu: !this.state.toggleMenu });
  };

  scroll = (y) => {
    console.log(y);
    window.scroll(y, y);
  };

  sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
  };

  render() {
    return (
      <div>
        <div className="contactoContainer">
          <button onClick={this.toggleNav} id="btnContacto">
            <img src={WhatsApp} alt="Extends button"></img>
          </button>
        </div>
        <form className="form" id="form" onSubmit={()=>this.sendEmail}ref={this.form}>
          <label htmlFor="user">
            Nombre & Apellido:{" "}
            <input type="text" id="user" name="user" required={true}></input>
          </label>
          <label htmlFor="email">E-mail de contacto: </label>
          <input type="email" id="email" name="email" required={true}></input>
          <textarea
            className="comment"
            rows={4}
            cols={1}
            name="comment"
            form="form"
            required={true}
            placeholder="¡Envíeme su mensaje!"
          >

          </textarea>
          <input type="submit" value="Enviar"></input>
        </form>
      </div>
    );
  }
}
