import { Component } from 'react';
import SobreMi from '../../img/SobreMi.png';
import FormacionAcademica from '../../img/FormacionAcademica.png';
import LenguajesYHerramientas from '../../img/LenguajesYHerramientas.png';
import Competencias from '../../img/Competencias.png';
import Experiencia from '../../img/Experiencia.png';
import OpenClose from '../../img/OpenNClose.png';
import './NavBar.css';


export default class NavBar extends Component{
    
    state={
        toggleMenu : false,
        screenWidth : window.innerWidth
    }

    toggleNav = () => {
        this.setState({toggleMenu : !this.state.toggleMenu})
    }

    screenWidth = () => {

    }

    render(){
        return(
            <nav className='NavBarContainer'>
                <a className='aNavBar' href='/'>
                    <h1>Analie Flórez Sánchez</h1>
                    <h2>Desarrollo Full Stack</h2>
                </a>
                <button className='btnNavBar'><img src={OpenClose} alt={'Menú'} onClick={this.toggleNav}></img></button>                

                {(this.state.toggleMenu || this.state.screenWidth > 500) &&
                <ul className='UlOrder'>
                    <li className='List'><img src={SobreMi} alt="Sobre mí"/><h1>&lt;Sobre mí/&gt;</h1> </li>
                    <li className='List'><img src={FormacionAcademica} alt="Formación Académica"/><h1>&lt;Formación/&gt;</h1></li>
                    <li className='List'><img src={LenguajesYHerramientas} alt="Lenguajes y Herramientas"/><h1>&lt;Lenguajes/&gt;</h1></li>
                    <li className='List'><img src={Competencias} alt="Competencias"/><h1>&lt;Competencias/&gt;</h1></li>
                    <li className='List'><img src={Experiencia} alt="Experiencia"/><h1>&lt;Experiencia/&gt;</h1></li>
                </ul>
                }
            </nav>
              )
    }
}
