import { Component } from "react";
import SENA from "../../img/SENA.png";
import Politecnico from "../../img/Politecnico.png";
import Udemy from "../../img/Udemy.png";
import SheWorks from "../../img/SheWorks.png";
import "./Formacion.css";

export default class Formacion extends Component {
   
  state = {
    SENA: false,
    Politecnico: false,
    Udemy: false,
    SheWorks: false,
    visible : false

  };

  openWindow = (e) => {
    if (e === 1) {
      this.setState({ SENA: true });
      this.setState({ Politecnico: false });
      this.setState({ Udemy: false });
      this.setState({ SheWorks: false });
    } else if (e === 2) {
      this.setState({ SENA: false });
      this.setState({ Politecnico: true });
      this.setState({ Udemy: false });
      this.setState({ SheWorks: false });
    } else if (e === 3) {
      this.setState({ SENA: false });
      this.setState({ Politecnico: false });
      this.setState({ Udemy: true });
      this.setState({ SheWorks: false });
    } else {
      this.setState({ SENA: false });
      this.setState({ Politecnico: false });
      this.setState({ Udemy: false });
      this.setState({ SheWorks: true });
    }
  };

  changeVisible = () => {
    this.setState({visible : !this.state.visible});
}

  render() {
    return (
      <div className="titleFormaciÃ³n">
        <div className="list">
        {this.state.visible&&<ul id='logosMenu'>
          <li onClick={() => {
                this.openWindow(1);
              }} className="logo sena">
           <h2>SENA</h2>
          </li>
          <li onClick={() => {
                this.openWindow(2);
              }} className="logo pequeÃ±o">
            <h2>Politecnico</h2>
          </li>
          <li onClick={() => {
                this.openWindow(3);
              }} className="logo">
            <h2>Udemy</h2>
           
          </li>
          <li className="logo pequeÃ±o">
            <img
              onClick={() => {
                this.openWindow(4);
              }}
              src={SheWorks}
              alt="SheWorks Logo"
            ></img>
          </li>
        </ul>}
        {(this.state.visible && this.state.SENA)&&<div id='Formacion'>
            <h1> <img
              src={SENA}
              alt="SENA Logo"
            ></img>SENA</h1>
            <h2>Servicio Nacional de Aprendizaje</h2>
            <h3>Cursos destacados: </h3>
            <p> Requisitos Funcionales y no Funcionales | JAVA | Bases de Datos | Algoritmos avanzados (JAVA)</p>
            </div>}
        {(this.state.visible && this.state.Politecnico)&&<div><img              
              src={Politecnico}
              alt="Politecnico Logo"
            ></img>Politecnico</div>}
        {(this.state.visible && this.state.Udemy)&&<div> <img              
              src={Udemy}
              alt="Udemy Logo"
            ></img>Udemy</div>}
        {this.state.SheWorks&&<div>SheWorks</div>}

        <div>
        {!this.state.visible&&<div id='subtitle'>
                    <h1>FormaciÃ³n</h1>
                    <h2>Â¡Selecciona abrir, luego el logo y mira los cursos destacados!</h2>
                </div>}
                <button id='btnFormacion' onClick={this.changeVisible}>Abrir</button>
                </div>
        </div>

        
      </div>
    );
  }
}
