import { Component } from "react";
import WhatsApp from "../../img/whatsapp-svgrepo-com.svg";
import "./Contacto.css";

export default class Contacto extends Component {
  state = {
    toggleMenu: false,
    screenWidth: window.innerWidth,
    animate: false,
  };

  toggleNav = () => {
    this.setState({ animate: true });
    this.setState({ toggleMenu: !this.state.toggleMenu });
  };

  scroll = (y) => {
    console.log(y);
    window.scroll(y, y);
  };

  render() {
    return (
      <div>
        <div className="contactoContainer">
          <button onClick={this.toggleNav} id="btnContacto">
            <img src={WhatsApp} alt="Extends button"></img>
          </button>
        </div>
        <form className="form" id="form">
          <label htmlFor="user">
            Nombre & Apellido:{" "}
            <input type="text" id="user" name="user" min="3" max="150"></input>
          </label>
          <label htmlFor="email">
            E-mail de contacto:{" "}
          </label>
          <input type="email" id="email" name="email"></input>

        <textarea className="comment" rows={4} cols={1} name="comment" form="form">¡Envíame tu comentario!</textarea>
          <input type="submit" value="Enviar"></input>
        </form>
      </div>
    );
  }
}
