import { Component } from "react";
import SENA from "../../img/SENA.png";
import Politecnico from "../../img/Politecnico.png";
import Udemy from "../../img/Udemy.png";
import SheWorks from "../../img/SheWorks.png";
import "./Formacion.css";

export default class Formacion extends Component {
   
  state = {
    SENA: true,
    Politecnico: false,
    Udemy: false,
    SheWorks: false,
    visible : false

  };

  openWindow = (e) => {
    if (e === 1) {
      this.setState({ SENA: true });
      this.setState({ Politecnico: false });
      this.setState({ Udemy: false });
      this.setState({ SheWorks: false });
    } else if (e === 2) {
      this.setState({ SENA: false });
      this.setState({ Politecnico: true });
      this.setState({ Udemy: false });
      this.setState({ SheWorks: false });
    } else if (e === 3) {
      this.setState({ SENA: false });
      this.setState({ Politecnico: false });
      this.setState({ Udemy: true });
      this.setState({ SheWorks: false });
    } else {
      this.setState({ SENA: false });
      this.setState({ Politecnico: false });
      this.setState({ Udemy: false });
      this.setState({ SheWorks: true });
    }
  };

  changeVisible = () => {
    this.setState({visible : !this.state.visible});
}

  render() {
    return (
      <div className="titleFormación">
        <div>
        <ul className="list">
          <li className="logo sena">
            <img
              onClick={() => {
                this.openWindow(1);
              }}
              src={SENA}
              alt="SENA Logo"
            ></img>
          </li>
          <li className="logo pequeño">
            <img
              onClick={() => {
                this.openWindow(2);
              }}
              src={Politecnico}
              alt="Politecnico Logo"
            ></img>
          </li>
          <li className="logo">
            <img
              onClick={() => {
                this.openWindow(3);
              }}
              src={Udemy}
              alt="Udemy Logo"
            ></img>
          </li>
          <li className="logo pequeño">
            <img
              onClick={() => {
                this.openWindow(4);
              }}
              src={SheWorks}
              alt="SheWorks Logo"
            ></img>
          </li>
        </ul>
        {!this.state.visible&&<div id='Formacion'>
                    <h1>Formación</h1>
                    <h2>¡Soy la persona ideal para su empresa!</h2>
                </div>}
                <button id='btnFormacion' onClick={this.changeVisible}>Abrir</button>
        </div>

        {this.state.SENA&&<div>
            <h1>SENA</h1>
            <h2>Servicio Nacional de Aprendizaje</h2>
            <h3>Cursos destacados: </h3>
            <p> Requisitos Funcionales y no Funcionales | JAVA | Bases de Datos | Algoritmos avanzados (JAVA)</p>
            </div>}
        {this.state.Politecnico&&<div>Politecnico</div>}
        {this.state.Udemy&&<div>Udemy</div>}
        {this.state.SheWorks&&<div>SheWorks</div>}
      </div>
    );
  }
}
